public class Output2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int year = 1;
		
		System.out.println("이름: 조병철");
		System.out.println("나이: " + (33 + year));
		System.out.println("이름: 김병철");
		System.out.println("나이: " + (34 + year));
		System.out.println("이름: 최병철");
		System.out.println("나이: " + (35 + year));
		System.out.println("이름: 이병철");
		System.out.println("나이: " + (36 + year));
	}

}
